mapper.update:
update usage_statistics set material_usage=#{materialUsage},
created_time=now()
where id=#{id}

mapper.delete:
delete from usage_statistics where user_id=#{userId,jdbcType=BIGINT} and
version=#{version,jdbcType=VARCHAR}
and flag=#{flag,jdbcType=INTEGER}
and material_id in

    #{materialId,jdbcType=INTEGER}

or parent_id in

    #{materialId,jdbcType=INTEGER}

mapper.select:
SELECT * FROM
usage_statistics u
WHERE u.form_id=#{formId}
and u.user_id=#{userId} AND u.version=#{version} AND u.flag=#{flag} AND u.parent_id=#{parentId}

SELECT* FROM
usage_statistics
WHERE
1 = 1

    and material_id = #{materialId,jdbcType=INTEGER}


    and material_name = #{materialName,jdbcType=VARCHAR}


    and parent_id = #{parentId,jdbcType=INTEGER}


    and flag = #{flag,jdbcType=INTEGER}


    and version = #{version,jdbcType=VARCHAR}


    and user_id = #{userId,jdbcType=BIGINT}


    and form_id = #{formId,jdbcType=INTEGER}
